mapper.insert:
INSERT INTO tb_member (

) VALUES (
#{mem_seq}
, #{mem_id}
, #{mem_pwd}
, #{mem_pwd_salt}
, #{mem_name}
, #{mem_gender}
, NOW()
, #{mem_last_login}
, Y
)

mapper.update:
UPDATE tb_member
SET mem_last_login = NOW()
, mem_name = #{mem_name}
, mem_gender = #{mem_gender}

    , mem_pwd = #{mem_pwd}

WHERE mem_id = #{mem_id}

mapper.delete:
UPDATE tb_member
SET mem_status = N
WHERE mem_id = #{mem_id}

mapper.select:
SELECT count(*)
FROM tb_member
WHERE mem_id = #{value}
